import * as R from 'ramda'

import categories from 'api/mockCategories'
import request from 'superagent'
import scanners from 'api/mockScanners'

export const fetchScanners = async () => {
  const {body} = await request.get(
    'https://run.mocky.io/v3/9d872ac1-f4d0-4637-a784-290d804b6298'
  )
  return body.scanners
}

export const loadMoreScanners = async ({offset}) => {
  return new Promise(resolve => {
    resolve(scanners)
  })
}

export const fetchScannerById = async (id) => {
  return new Promise(resolve => {
    const scanner = R.find(R.propEq('id', id), scanners)
    resolve(scanner)
  })
}

export const fetchCategories = async () => {
  return new Promise(resolve => {
    resolve(categories)
  })
}
