import {
  getTotalBasketCount,
  getTotalBasketPrice
} from 'selectors'

import {Link} from 'react-router-dom'
import React from 'react'
import {connect} from 'react-redux'

const BasketCart = ({totalBasketCount, totalPrice}) => {
  return (
    <div className='cart'>
      <div className='dropdown'>
        <Link
          to='/basket'
          id='dLabel'
          className='btn btn-inverse btn-block btn-lg cart__btn'
        >
          <i className='fa fa-fa-shopping-cart'></i>
          <span>Корзина ({totalBasketCount})</span>
        </Link>
      </div>
    </div>
  )
}

const mapStateToProps = state => {
  return {
    totalBasketCount: getTotalBasketCount(state),
    totalPrice: getTotalBasketPrice(state)
  }
}

export default connect(mapStateToProps, null)(BasketCart)