import * as R from 'ramda'

import React, {Component} from 'react'
import {
  addScannerToBasket,
  fetchCategories,
  fetchScanners,
  loadMoreScanners
} from 'actions'

import Layout from 'containers/layout'
import {Link} from 'react-router-dom'
import {connect} from 'react-redux'
import {getScanners} from 'selectors'

class scanners extends Component {
  componentDidMount() {
    this.props.fetchScanners()
    this.props.fetchCategories()
  }

  renderScanner(scanner, index) {
    const {addScannerToBasket} = this.props
    const shortDescription = `${R.take(60, scanner.description)}...`

    return (
      <div className='col-sm-4 col-lg-4 col-md-4 book-list list__item' key={index}>
        <div className='thumbnail list__item-inner'>
          <img
            className='img-thumbnail list__item-img'
            src={scanner.image}
            alt={scanner.name}
          />
          
            <div className="list__item-price-name">
              <h4 className=" list__item-name">
                <Link to={`/scanners/${scanner.id}`}>
                  {scanner.name} 
                </Link>
              </h4>
              <h4 className='list__item-price'>{scanner.price} ₽</h4>
            </div>
            
            <p className='list__item-info'>{shortDescription}</p>
            <p className='itemButton'>
              <button
                className='btn btn-primary list__item-first-btn'
                onClick={() => addScannerToBasket(scanner.id)}
              >
                Купить
              </button>
              <Link
                to={`/scanners/${scanner.id}`}
                className='btn btn-default list__item-two-btn'
              >
               Подробнее
              </Link>
            </p>
          </div>
        </div>
      
    )
  }

  render() {
    const {scanners, loadMoreScanners} = this.props
    return (
      <Layout>
        <div className='books row'>
          {scanners.map((scanner, index) => this.renderScanner(scanner, index))}
        </div>
        <div className='row'>
          <div className='col-md-12'>
            <button
              onClick={loadMoreScanners}
              className='pull-right btn btn-primary'
            >
              Загрузить каталог
            </button>
          </div>
        </div>
        
      </Layout>
    )
  }
}

const mapStateToProps = (state, ownProps) => ({
  scanners: getScanners(state, ownProps)
})
window.onload = function(){if (window.performance) {
  if (performance.navigation.type == 1) {
    
   document.querySelector(".btn-primary").click();
  } else {
    document.querySelector(".btn-primary").click();
  }
}
}
const mapDispatchToProps = {
  fetchScanners,
  loadMoreScanners,
  addScannerToBasket,
  fetchCategories
}

export default connect(mapStateToProps, mapDispatchToProps)(scanners)
