import * as R from 'ramda'

import {
  FETCH_SCANNERS_SUCCESS,
  LOAD_MORE_SCANNERS_SUCCESS,
  SEARCH_SCANNER
} from 'actionTypes'

const initialState = {
  ids: [],
  search: ''
}

export default (state = initialState, {type, payload}) => {
  switch (type) {
    case FETCH_SCANNERS_SUCCESS:
      return R.merge(state, {
        ids: R.pluck('id', payload)
      })
    case LOAD_MORE_SCANNERS_SUCCESS:
      const ids = R.pluck('id', payload)
      return R.merge(state, {
        ids: R.concat(ids, state.ids)
      })
    case SEARCH_SCANNER:
      return R.merge(state, {
        search: payload
      })
    default:
      return state;
  }
}