import * as R from 'ramda'

export const getScannerById = (state, id) => R.prop(id, state.scanners)

export const getScanners = (state, ownProps) => {
  const activeCategoryId = getActiveCategoryId(ownProps)
  const applySearch = item => R.contains(
    state.scannersPage.search,
    R.prop('name', item)
  )
  const applyCategory = item => R.equals(
    activeCategoryId,
    R.prop('categoryId', item)
  )
  const scanners = R.compose(
    R.filter(applySearch),
    R.when(R.always(activeCategoryId), R.filter(applyCategory)),
    R.map(id => getScannerById(state, id))
  )(state.scannersPage.ids)
  return scanners
}

export const getRenderedScannersLength = state => R.length(state.scannersPage.ids)

export const getTotalBasketCount = state => R.length(state.basket)

export const getTotalBasketPrice = state => {
  const totalPrice = R.compose(
    R.sum,
    R.pluck('price'),
    R.map(id => getScannerById(state, id))
  )(state.basket)

  return totalPrice
}

export const getCategories = state => R.values(state.categories)

export const getActiveCategoryId = ownProps => R.path(['match', 'params', 'id'], ownProps)

export const getBasketscannersWithCount = state => {
  const scannerCount = id => R.compose(
    R.length,
    R.filter(basketId => R.equals(id, basketId))
  )(state.basket)
  const scannerWithCount = scanner => R.assoc('count', scannerCount(scanner.id), scanner)
  const uniqueIds = R.uniq(state.basket)
  const scanners = R.compose(
    R.map(scannerWithCount),
    R.map(id => getScannerById(state, id))
  )(uniqueIds)

  return scanners
}